$(document).on('click', '#menu', function () {
    if ($('#menu-container').is(':visible')) {
        $('#menu').removeClass('fa-times').addClass('fa-bars');
    } else {
        $('#menu').removeClass('fa-bars').addClass('fa-times');
    }
    $('#menu-container').fadeToggle(250);
})

